import React, { useState, useEffect } from 'react';
import { io } from 'socket.io-client';
import axios from 'axios';
import {
  Container,
  Paper,
  TextField,
  Button,
  Box,
  Typography,
  List,
  ListItem,
  ListItemText,
} from '@mui/material';

const API_URL = 'https://pipaan.com360degree.com/api/group_chat/add-group_chat';
const TOKEN = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJzdWIiOjE5NywiaWF0IjoxNzQ2MzU4MzU2LCJleHAiOjE3NTE1NDIzNTYsInR5cGUiOiJyZWZyZXNoIn0.balsgBuA-1dotW-i-aOSgNuAHcul6JTyRqcJXnvsckQ';

function App() {
  const [socket, setSocket] = useState(null);
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const [connected, setConnected] = useState(false);

  useEffect(() => {
    // Initialize socket connection
    const newSocket = io('https://pipaan.com360degree.com/', {
      auth: {
        token: TOKEN
      }
    });

    newSocket.on('connect', () => {
      setConnected(true);
      console.log('Connected to WebSocket');
    });

    newSocket.on('disconnect', () => {
      setConnected(false);
      console.log('Disconnected from WebSocket');
    });

    newSocket.on('message', (msg) => {
      setMessages(prev => [...prev, msg]);
    });

    setSocket(newSocket);

    return () => {
      newSocket.close();
    };
  }, []);

  const sendMessage = async () => {
    if (!message.trim()) return;

    const payload = {
      message: message,
      reciever_id: 197,
      media_url: [],
      is_group_chat: true
    };

    try {
      const response = await axios.post(API_URL, payload, {
        headers: {
          'Token': `${TOKEN}`,
          'Content-Type': 'application/json'
        }
      });

      if (response.status === 200) {
        socket?.emit('message', {
          message: message,
          timestamp: new Date().toISOString(),
          sender: 'You'
        });
        setMessage('');
      }
    } catch (error) {
      console.error('Error sending message:', error);
    }
  };

  return (
    <Container maxWidth="md" sx={{ py: 4 }}>
      <Paper elevation={3} sx={{ p: 3 }}>
        <Typography variant="h4" gutterBottom>
          Chat Test
        </Typography>

        <Box sx={{ mb: 2 }}>
          <Typography variant="body2" color={connected ? 'success.main' : 'error.main'}>
            Status: {connected ? 'Connected' : 'Disconnected'}
          </Typography>
        </Box>

        <Paper 
          elevation={1} 
          sx={{ 
            height: '400px', 
            mb: 2, 
            p: 2, 
            overflow: 'auto',
            backgroundColor: '#f5f5f5'
          }}
        >
          <List>
            {messages.map((msg, index) => (
              <ListItem key={index}>
                <ListItemText
                  primary={msg.message}
                  secondary={`${msg.sender} - ${new Date(msg.timestamp).toLocaleTimeString()}`}
                />
              </ListItem>
            ))}
          </List>
        </Paper>

        <Box sx={{ display: 'flex', gap: 1 }}>
          <TextField
            fullWidth
            variant="outlined"
            placeholder="Type your message..."
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            onKeyPress={(e) => e.key === 'Enter' && sendMessage()}
          />
          <Button 
            variant="contained" 
            onClick={sendMessage}
            disabled={!connected}
          >
            Send
          </Button>
        </Box>
      </Paper>
    </Container>
  );
}

export default App;
