import React, { useState, useEffect } from 'react';
import { io } from 'socket.io-client';
import axios from 'axios';
import {
  Container,
  Paper,
  TextField,
  Button,
  Box,
  Typography,
  List,
  ListItem,
  ListItemText,
  ToggleButton,
  ToggleButtonGroup,
} from '@mui/material';

const API_URL = 'https://pipaan.com360degree.com/api/group_chat/add-group_chat';

const USERS = {
  USER1: {
    id: 197,
    token: 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJzdWIiOjE5NywiaWF0IjoxNzQ2MzU4MzU2LCJleHAiOjE3NTE1NDIzNTYsInR5cGUiOiJyZWZyZXNoIn0.balsgBuA-1dotW-i-aOSgNuAHcul6JTyRqcJXnvsckQ',
    name: 'User 197'
  },
  USER2: {
    id: 199,
    token: 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJzdWIiOjE5OSwiaWF0IjoxNzQ2MzYyMjI1LCJleHAiOjE3NTE1NDYyMjUsInR5cGUiOiJyZWZyZXNoIn0.Bn9l9PhIqVhxARGEOFKuVWgIi_ieVxIwJu-Pxgn6su4',
    name: 'User 199'
  }
};

function App() {
  const [socket, setSocket] = useState(null);
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const [connected, setConnected] = useState(false);
  const [activeUser, setActiveUser] = useState('USER1');

  useEffect(() => {
    // Initialize socket connection
    const newSocket = io('https://pipaan.com360degree.com/', {
      auth: {
        token: USERS[activeUser].token
      }
    });

    newSocket.on('connect', () => {
      setConnected(true);
      console.log('Connected to WebSocket as', USERS[activeUser].name);
    });

    newSocket.on('disconnect', () => {
      setConnected(false);
      console.log('Disconnected from WebSocket');
    });

    // Listen for messages where I am the receiver
    newSocket.on('message', (msg) => {
      if (msg.receiver_id === USERS[activeUser].id) {
        setMessages(prev => [...prev, {
          ...msg,
          sender: msg.sender_id === USERS[activeUser].id ? 'You' : `User ${msg.sender_id}`
        }]);
      }
    });

    setSocket(newSocket);

    return () => {
      newSocket.close();
    };
  }, [activeUser]);

  const handleUserChange = (event, newUser) => {
    if (newUser !== null) {
      setActiveUser(newUser);
      setMessages([]); // Clear messages when switching users
    }
  };

  const sendMessage = async () => {
    if (!message.trim()) return;

    const currentUser = USERS[activeUser];
    const receiverId = activeUser === 'USER1' ? USERS.USER2.id : USERS.USER1.id;

    const payload = {
      message: message,
      reciever_id: receiverId,
      media_url: [],
      is_group_chat: true
    };

    try {
      const response = await axios.post(API_URL, payload, {
        headers: {
          'Token': currentUser.token,
          'Content-Type': 'application/json'
        }
      });

      if (response.status === 200) {
        // Add the message to the local state immediately
        setMessages(prev => [...prev, {
          message: message,
          timestamp: new Date().toISOString(),
          sender: 'You',
          sender_id: currentUser.id,
          receiver_id: receiverId
        }]);

        // Emit the message through socket
        socket?.emit('message', {
          message: message,
          timestamp: new Date().toISOString(),
          sender_id: currentUser.id,
          receiver_id: receiverId
        });

        setMessage('');
      }
    } catch (error) {
      console.error('Error sending message:', error);
    }
  };

  return (
    <Container maxWidth="md" sx={{ py: 4 }}>
      <Paper elevation={3} sx={{ p: 3 }}>
        <Box sx={{ mb: 3, display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          <Typography variant="h4">
            Chat Test
          </Typography>
          <ToggleButtonGroup
            value={activeUser}
            exclusive
            onChange={handleUserChange}
            aria-label="user selection"
          >
            <ToggleButton value="USER1" aria-label="user 197">
              User 197
            </ToggleButton>
            <ToggleButton value="USER2" aria-label="user 199">
              User 199
            </ToggleButton>
          </ToggleButtonGroup>
        </Box>

        <Box sx={{ mb: 2 }}>
          <Typography variant="body2" color={connected ? 'success.main' : 'error.main'}>
            Status: {connected ? `Connected as ${USERS[activeUser].name}` : 'Disconnected'}
          </Typography>
        </Box>

        <Paper 
          elevation={1} 
          sx={{ 
            height: '400px', 
            mb: 2, 
            p: 2, 
            overflow: 'auto',
            backgroundColor: '#f5f5f5'
          }}
        >
          <List>
            {messages.map((msg, index) => (
              <ListItem 
                key={index}
                sx={{
                  justifyContent: msg.sender === 'You' ? 'flex-end' : 'flex-start'
                }}
              >
                <ListItemText
                  primary={msg.message}
                  secondary={`${msg.sender} - ${new Date(msg.timestamp).toLocaleTimeString()}`}
                  sx={{
                    textAlign: msg.sender === 'You' ? 'right' : 'left',
                    backgroundColor: msg.sender === 'You' ? '#e3f2fd' : '#f5f5f5',
                    padding: '8px 12px',
                    borderRadius: '8px',
                    maxWidth: '70%'
                  }}
                />
              </ListItem>
            ))}
          </List>
        </Paper>

        <Box sx={{ display: 'flex', gap: 1 }}>
          <TextField
            fullWidth
            variant="outlined"
            placeholder={`Type your message as ${USERS[activeUser].name}...`}
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            onKeyPress={(e) => e.key === 'Enter' && sendMessage()}
          />
          <Button 
            variant="contained" 
            onClick={sendMessage}
            disabled={!connected}
          >
            Send
          </Button>
        </Box>
      </Paper>
    </Container>
  );
}

export default App;
