import React, { useState, useEffect } from 'react';
import { io } from 'socket.io-client';
import axios from 'axios';
import {
  Container,
  Paper,
  TextField,
  Button,
  Box,
  Typography,
} from '@mui/material';

const API_URL = 'https://pipaan.com360degree.com/api/group_chat/add-group_chat';
const SOCKET_URL = 'https://pipaan.com360degree.com/';

const USER = {
  id: 199,
  token: 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJzdWIiOjE5OSwiaWF0IjoxNzQ2MzYyMjI1LCJleHAiOjE3NTE1NDYyMjUsInR5cGUiOiJyZWZyZXNoIn0.Bn9l9PhIqVhxARGEOFKuVWgIi_ieVxIwJu-Pxgn6su4',
  name: 'User 199'
};

function App() {
  const [socket, setSocket] = useState(null);
  const [messages, setMessages] = useState([]);
  const [message, setMessage] = useState('');
  const [connected, setConnected] = useState(false);

  useEffect(() => {
    const newSocket = io(SOCKET_URL, {
      auth: {
        token: USER.token
      }
    });

    // Register user
    newSocket.emit('addUser', USER.id);
    console.log('Registered user with ID:', USER.id);

    newSocket.on('connect', () => {
      setConnected(true);
      console.log('Connected to WebSocket with ID:', newSocket.id);
      // Join both group and user rooms
      newSocket.emit('joinRoom', `group_${USER.id}`);
      newSocket.emit('joinRoom', `user_${USER.id}`);
      console.log(`Joined rooms for user ${USER.id}`);
    });

    newSocket.on('disconnect', () => {
      setConnected(false);
      console.log('Disconnected from WebSocket');
    });

    // Listen for messages with the correct event name from backend
    newSocket.on('receive-message', (msg) => {
      console.log('Received message event triggered:', msg);
      setMessages(prev => [...prev, {
        ...msg,
        timestamp: msg.timestamp || new Date().toISOString()
      }]);
    });

    // Also listen for 'message' event
    newSocket.on('message', (msg) => {
      console.log('Message event received:', msg);
      setMessages(prev => [...prev, {
        ...msg,
        timestamp: msg.timestamp || new Date().toISOString()
      }]);
    });

    // Listen for message acknowledgment
    newSocket.on('messageReceived', (msg) => {
      console.log('Message received acknowledgment:', msg);
    });

    // Add error handler
    newSocket.on('error', (error) => {
      console.error('Socket error:', error);
    });

    // Add connect_error handler
    newSocket.on('connect_error', (error) => {
      console.error('Connection error:', error);
    });

    // Log all incoming events for debugging
    newSocket.onAny((eventName, ...args) => {
      console.log('Socket event received:', eventName, args);
    });

    setSocket(newSocket);

    return () => {
      newSocket.off('receive-message');
      newSocket.close();
    };
  }, []);

  const sendMessage = async () => {
    if (!message.trim()) return;

    const payload = {
      message: message,
      reciever_id: 197, // Sending to self
      media_url: [],
      is_group_chat: true
    };

    try {
      // Send message through API
      const response = await axios.post(API_URL, payload, {
        headers: {
          'Token': USER.token,
          'Content-Type': 'application/json'
        }
      });

      if (response.status === 200) {
        const newMessage = {
          message: message,
          timestamp: new Date().toISOString(),
          sender_id: USER.id,
          reciever_id: 197,
          is_group_chat: true
        };

        // Add message to messages immediately
        setMessages(prev => [...prev, newMessage]);

        // Emit the message through socket
        socket?.emit('sendMessage', newMessage);
        // Also try sending with 'message' event
        socket?.emit('message', newMessage);
        console.log('Message emitted through socket:', newMessage);

        console.log('Message sent successfully');
        setMessage('');
      }
    } catch (error) {
      console.error('Error sending message:', error);
    }
  };

  return (
    <Container maxWidth="md" sx={{ py: 4 }}>
      <Typography variant="h4" gutterBottom align="center" sx={{ mb: 4 }}>
        Chat Test - Single User
      </Typography>

      <Paper elevation={3} sx={{ p: 3, height: '80vh', display: 'flex', flexDirection: 'column' }}>
        <Typography variant="h5" gutterBottom>
          {USER.name}
        </Typography>

        <Box sx={{ mb: 2 }}>
          <Typography variant="body2" color={connected ? 'success.main' : 'error.main'}>
            Status: {connected ? 'Connected' : 'Disconnected'}
          </Typography>
        </Box>

        <Paper 
          elevation={1} 
          sx={{ 
            flex: 1,
            mb: 2, 
            p: 2, 
            overflow: 'auto',
            backgroundColor: '#f5f5f5'
          }}
        >
          {messages.map((msg, index) => (
            <Box
              key={index}
              sx={{
                textAlign: msg.sender_id === USER.id ? 'right' : 'left',
                mb: 1
              }}
            >
              <Typography
                sx={{
                  display: 'inline-block',
                  backgroundColor: msg.sender_id === USER.id ? '#e3f2fd' : '#f5f5f5',
                  padding: '8px 12px',
                  borderRadius: '8px',
                  maxWidth: '70%'
                }}
              >
                <Typography variant="subtitle2" color="textSecondary">
                  {msg.sender_id === USER.id ? 'You' : `User ${msg.sender_id}`}
                </Typography>
                <Typography>{msg.message}</Typography>
                <Typography variant="caption" color="textSecondary">
                  {new Date(msg.timestamp).toLocaleTimeString()}
                </Typography>
              </Typography>
            </Box>
          ))}
        </Paper>

        <Box sx={{ display: 'flex', gap: 1 }}>
          <TextField
            fullWidth
            variant="outlined"
            placeholder="Type your message..."
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            onKeyPress={(e) => e.key === 'Enter' && sendMessage()}
          />
          <Button 
            variant="contained" 
            onClick={sendMessage}
            disabled={!connected}
          >
            Send
          </Button>
        </Box>
      </Paper>
    </Container>
  );
}

export default App;
