import React, { useState, useEffect } from 'react';
import { io } from 'socket.io-client';
import axios from 'axios';
import {
  Container,
  Paper,
  TextField,
  Button,
  Box,
  Typography,
  Grid,
} from '@mui/material';

const API_URL = 'https://pipaan.com360degree.com/api/group_chat/add-group_chat';
const SOCKET_URL = 'https://pipaan.com360degree.com/';

const USERS = {
  USER1: {
    id: 197,
    token: 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJzdWIiOjE5NywiaWF0IjoxNzQ2MzU4MzU2LCJleHAiOjE3NTE1NDIzNTYsInR5cGUiOiJyZWZyZXNoIn0.balsgBuA-1dotW-i-aOSgNuAHcul6JTyRqcJXnvsckQ',
    name: 'User 197'
  },
  USER2: {
    id: 199,
    token: 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJzdWIiOjE5OSwiaWF0IjoxNzQ2MzYyMjI1LCJleHAiOjE3NTE1NDYyMjUsInR5cGUiOiJyZWZyZXNoIn0.Bn9l9PhIqVhxARGEOFKuVWgIi_ieVxIwJu-Pxgn6su4',
    name: 'User 199'
  }
};

function ChatWindow({ user, messages, onSendMessage, connected }) {
  const [message, setMessage] = useState('');

  const handleSend = () => {
    if (message.trim()) {
      onSendMessage(message, user);
      setMessage('');
    }
  };

  return (
    <Paper elevation={3} sx={{ p: 3, height: '100%', display: 'flex', flexDirection: 'column' }}>
      <Typography variant="h5" gutterBottom>
        {user.name}
      </Typography>

      <Box sx={{ mb: 2 }}>
        <Typography variant="body2" color={connected ? 'success.main' : 'error.main'}>
          Status: {connected ? 'Connected' : 'Disconnected'}
        </Typography>
      </Box>

      <Paper 
        elevation={1} 
        sx={{ 
          flex: 1,
          mb: 2, 
          p: 2, 
          overflow: 'auto',
          backgroundColor: '#f5f5f5'
        }}
      >
        {messages.map((msg, index) => (
          <Box
            key={index}
            sx={{
              textAlign: msg.senderId === user.id ? 'right' : 'left',
              mb: 1
            }}
          >
            <Typography
              sx={{
                display: 'inline-block',
                backgroundColor: msg.senderId === user.id ? '#e3f2fd' : '#f5f5f5',
                padding: '8px 12px',
                borderRadius: '8px',
                maxWidth: '70%'
              }}
            >
              <Typography variant="subtitle2" color="textSecondary">
                {msg.senderId === user.id ? 'You' : `User ${msg.senderId}`}
              </Typography>
              <Typography>{msg.message}</Typography>
              <Typography variant="caption" color="textSecondary">
                {new Date(msg.timestamp).toLocaleTimeString()}
              </Typography>
            </Typography>
          </Box>
        ))}
      </Paper>

      <Box sx={{ display: 'flex', gap: 1 }}>
        <TextField
          fullWidth
          variant="outlined"
          placeholder={`Type your message as ${user.name}...`}
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          onKeyPress={(e) => e.key === 'Enter' && handleSend()}
        />
        <Button 
          variant="contained" 
          onClick={handleSend}
          disabled={!connected}
        >
          Send
        </Button>
      </Box>
    </Paper>
  );
}

function App() {
  const [sockets, setSockets] = useState({});
  const [messages, setMessages] = useState({ USER1: [], USER2: [] });
  const [connected, setConnected] = useState({ USER1: false, USER2: false });

  useEffect(() => {
    const newSockets = {};

    Object.entries(USERS).forEach(([userKey, user]) => {
      const socket = io(SOCKET_URL, {
        auth: {
          token: user.token
        }
      });

      // Register user
      socket.emit('addUser', user.id);

      socket.on('connect', () => {
        setConnected(prev => ({ ...prev, [userKey]: true }));
        console.log(`Connected to WebSocket as ${user.name}`);
      });

      socket.on('disconnect', () => {
        setConnected(prev => ({ ...prev, [userKey]: false }));
        console.log(`Disconnected from WebSocket as ${user.name}`);
      });

      // Listen for messages
      socket.on('receiveMessage', (msg) => {
        console.log(`Received message for ${user.name}:`, msg);
        const receiverKey = msg.receiverId === USERS.USER1.id ? 'USER1' : 'USER2';

        setMessages(prev => ({
          ...prev,
          [receiverKey]: [...prev[receiverKey], {
            ...msg,
            timestamp: msg.timestamp || new Date().toISOString()
          }]
        }));
      });

      newSockets[userKey] = socket;
    });

    setSockets(newSockets);

    return () => {
      Object.values(newSockets).forEach(socket => {
        socket.off('receiveMessage');
        socket.close();
      });
    };
  }, []);

  const sendMessage = async (message, senderUser) => {
    const receiverKey = senderUser === USERS.USER1 ? 'USER2' : 'USER1';
    const receiverId = USERS[receiverKey].id;
    const senderKey = senderUser === USERS.USER1 ? 'USER1' : 'USER2';

    const payload = {
      message: message,
      reciever_id: receiverId,
      media_url: [],
      is_group_chat: true
    };

    try {
      // Send message through API
      const response = await axios.post(API_URL, payload, {
        headers: {
          'Token': senderUser.token,
          'Content-Type': 'application/json'
        }
      });

      if (response.status === 200) {
        const newMessage = {
          message: message,
          timestamp: new Date().toISOString(),
          senderId: senderUser.id,
          receiverId: receiverId
        };

        // Add message to sender's messages immediately
        setMessages(prev => ({
          ...prev,
          [senderKey]: [...prev[senderKey], newMessage]
        }));

        // Emit the message through socket
        sockets[senderKey]?.emit('sendMessage', newMessage);

        console.log(`Message sent from ${senderUser.name} to User ${receiverId}`);
      }
    } catch (error) {
      console.error('Error sending message:', error);
    }
  };

  return (
    <Container maxWidth="xl" sx={{ py: 4 }}>
      <Typography variant="h4" gutterBottom align="center" sx={{ mb: 4 }}>
        Chat Test - Two Users
      </Typography>

      <Grid container spacing={3}>
        <Grid item xs={12} md={6}>
          <ChatWindow
            user={USERS.USER1}
            messages={messages.USER1}
            onSendMessage={sendMessage}
            connected={connected.USER1}
          />
        </Grid>
        <Grid item xs={12} md={6}>
          <ChatWindow
            user={USERS.USER2}
            messages={messages.USER2}
            onSendMessage={sendMessage}
            connected={connected.USER2}
          />
        </Grid>
      </Grid>
    </Container>
  );
}

export default App;
